<?php$BSP = filter_input(INPUT_POST, 'BoardSettingsSubmit', FILTER_SANITIZE_SPECIAL_CHARS);	$BSM = filter_input(INPUT_POST, 'BoardSettingsMySQL', FILTER_SANITIZE_SPECIAL_CHARS);	$BSI = filter_input(INPUT_POST, 'BoardSettingsImpressum', FILTER_SANITIZE_SPECIAL_CHARS);	if(isset($BSP)){	$boardname = filter_input(INPUT_POST, 'BoardName', FILTER_SANITIZE_SPECIAL_CHARS);			if(!empty($boardname))	{		Settings::SetSystemData("settings","BoardName",$boardname);	}	header('Location: ?page=Acp&show=BoardSettings');}if(isset($BSM)){	$MySQLHost = filter_input(INPUT_POST, 'MySQLHost', FILTER_SANITIZE_SPECIAL_CHARS);		$MySQLUser = filter_input(INPUT_POST, 'MySQLUser', FILTER_SANITIZE_SPECIAL_CHARS);		$MySQLPass = filter_input(INPUT_POST, 'MySQLPass', FILTER_SANITIZE_SPECIAL_CHARS);		$MySQLData = filter_input(INPUT_POST, 'MySQLData', FILTER_SANITIZE_SPECIAL_CHARS);			if(!empty($MySQLHost))	{		Settings::SetSystemDataToFile("sql-config.ini","host",$MySQLHost);	}	if(!empty($MySQLUser))	{		Settings::SetSystemDataToFile("sql-config.ini","user",$MySQLUser);	}	if(!empty($MySQLData))	{		Settings::SetSystemDataToFile("sql-config.ini","data",$MySQLData);	}	if(!empty($MySQLPass))	{		Settings::SetSystemDataToFile("sql-config.ini","pass",$MySQLPass);	}	header('Location: ?page=Acp&show=BoardSettings');}if(isset($BSI)){	$ImpName = filter_input(INPUT_POST, 'ImpName', FILTER_SANITIZE_SPECIAL_CHARS);		$ImpAnschrift = filter_input(INPUT_POST, 'ImpAnschrift', FILTER_SANITIZE_SPECIAL_CHARS);		$ImpOrt = filter_input(INPUT_POST, 'ImpOrt', FILTER_SANITIZE_SPECIAL_CHARS);		$ImpEmail = filter_input(INPUT_POST, 'ImpEmail', FILTER_SANITIZE_SPECIAL_CHARS);		$ImpTelefon = filter_input(INPUT_POST, 'ImpTelefon', FILTER_SANITIZE_SPECIAL_CHARS);			if(!empty($ImpName))	{		Settings::SetSystemData("impressum","Name",$ImpName);	}	if(!empty($ImpAnschrift))	{		Settings::SetSystemData("impressum","Anschrift",$ImpAnschrift);	}	if(!empty($ImpOrt))	{		Settings::SetSystemData("impressum","Ort",$ImpOrt);	}	if(!empty($ImpEmail))	{		Settings::SetSystemData("impressum","Email",$ImpEmail);	}	if(!empty($ImpTelefon))	{		Settings::SetSystemData("impressum","Telefon",$ImpTelefon);	}	header('Location: ?page=Acp&show=BoardSettings');}?>